from typing import List

from okdmr.dmrlib.hytera.pdu.hdap import HDAP


def test_hdap_frombytes():
    hexmessages: List[str] = [
        "02040005006400000001c403",
        "0204800600000f690600012903",
        "02c910050002000101014f03",
        "",
        "0241080500006f0000007503",
    ]
    for hexmsg in hexmessages:
        bytesmsg = bytes.fromhex(hexmsg)
        hdap = HDAP.from_bytes(bytesmsg)
        assert bytesmsg == hdap.as_bytes()
